	function numFunc(){
		var x = 10;
		return{
			getNum: function(){
				return x;
			},
			setNum: function(newNum){
				x = newNum;
			}
		};
	}
	var callFunc = numFunc();
	callFunc.getNum();
	callFunc.setNum(2);
	callFunc.getNum();
